import styles from "./Header.module.css"
import logo from "../../img/logo.png"
import { BurgerMenu } from "./BurgerMenu/BurgerMenu"
import { NavBar } from "./NavBar/NavBar"
import { useEffect, useRef, useState } from "react"

export const Header = () => {
    const ref = useRef<HTMLDivElement>(null)
    const navRef = useRef<HTMLDivElement>(null)

    const [isActive, setIsActive] = useState(false)

    useEffect(() => {
        const handleClick = (e: any) => {
            
            if (ref.current && !ref.current.contains(e.target) && navRef.current && !navRef.current.contains(e.target)) {
                setIsActive(false)
            }
        }

        document.addEventListener('click', handleClick)
        return () => {
            document.removeEventListener('click', handleClick)
        }
    }, [ isActive ])
    
    return (
        <header className={styles.header}>
            <div className={styles.headerContent}>
                <div className={styles.imgBlock}>
                    <img src={logo} className="adaptive-img" />
                </div>
                <div ref={ref} onClick={() => setIsActive(true)}>
                    <BurgerMenu isActive={isActive} />
                </div>
            </div>
            <div ref={navRef} className={isActive ? styles.navbarBlockOpened : styles.navbarBlock}>
                <NavBar />
            </div>
        </header>
    )
}
